import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Register from "./components/Register/Register";
import Menu from "./components/Home/Menu.js";
import HomeContent from "./components/Home/HomeContent.js";
import Login from "./components/Login/login.js";
import Footer from "./components/Home/Footer.js";
import CreateTeam from "./components/Navbar/CreateTeam.js";

// Hàm kiểm tra đăng nhập
const isAuthenticated = () => {
  // Thực hiện kiểm tra xem người dùng đã đăng nhập chưa.
  // Trong ví dụ này, bạn có thể sử dụng một trạng thái hoặc thông tin từ backend.
  // Ở đây, tôi sử dụng một trạng thái đơn giản để mô phỏng.
  const isLoggedIn = true; // Thay đổi giá trị này để phản ánh trạng thái đăng nhập của người dùng
  return isLoggedIn;
};

function App() {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/auth/register" element={<Register />} />
        <Route path="/auth/login" element={<Login />} />
        <Route
          path="/upload"
          element={
            isAuthenticated() ? (
              <CreateTeam />
            ) : (
              <Navigate to="/auth/login" replace />
            )
          }
        />
        <Route
          path="/"
          element={
            <>
              <Menu />
              <HomeContent />
              <Footer />
            </>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
