import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';

export default function Menu() {
    const [isLoggedIn, setIsLoggedIn] = useState(false); // State để kiểm soát trạng thái đăng nhập

    useEffect(() => {
        // Kiểm tra trạng thái đăng nhập ở đây
        // Nếu đăng nhập thành công, setIsLoggedIn(true);
    }, []); // Chỉ gọi useEffect một lần sau khi component được render

    const handleLogout = () => {
        // Xử lý đăng xuất ở đây
        setIsLoggedIn(false); // Đặt lại trạng thái đăng nhập sau khi đăng xuất thành công
    };

    return (
        <Navbar expand="lg" className="bg-body-tertiary sticky-top">
            <Container fluid>
                <Navbar.Brand href="#" >Logo</Navbar.Brand>
                <Navbar.Toggle aria-controls="navbarScroll" />
                <Navbar.Collapse id="navbarScroll">
                    <Nav
                        className="me-auto my-2 my-lg-0"
                        style={{ maxHeight: '100px' }}
                        navbarScroll
                    >
                        <Nav.Link href="#action1">Trang chủ</Nav.Link>
                        <NavDropdown title="Giải đấu" id="navbarScrollingDropdown" className='nav-link'>
                            <NavDropdown.Item href="#action3">Tìm giải đấu</NavDropdown.Item>
                            <NavDropdown.Item href="#action4">Tạo giải đấu</NavDropdown.Item>
                        </NavDropdown>
                        <NavDropdown title="Đội thi đấu" id="navbarScrollingDropdown" className='nav-link'>
                            <NavDropdown.Item href="#action3">Tạo đội</NavDropdown.Item>
                            <NavDropdown.Item href="#action3">Tìm đội</NavDropdown.Item>
                            <NavDropdown.Item href="#action4">Tạo đội hình</NavDropdown.Item>
                        </NavDropdown>
                        <Nav.Link href="#action2" className='nav-link'>Mua sắm</Nav.Link>
                    </Nav>
                    {isLoggedIn ? ( // Kiểm tra trạng thái đăng nhập để xác định hiển thị các liên kết
                        <Button variant="outline-danger" onClick={handleLogout}>Đăng xuất</Button>
                    ) : (
                        <>
                            <Nav.Link href="/auth/register">Đăng kí</Nav.Link>
                            <Nav.Link href="/auth/login">Đăng nhập</Nav.Link>
                        </>
                    )}
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
}
