// import React, { useState } from 'react';
import axios from 'axios';

export default function CreateTeam() {
    const [file, setFile] = useState(null);
    const [imageUrl, setImageUrl] = useState(null);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleSaveTeam = async () => {
        const formData = new FormData();
        formData.append('file', file);
      
        try {
          const response = await axios.post('http://localhost:3001/team/upload', formData);
      
          // Extract the Cloudinary image URL from the response data
          const imageUrl = response.data.imageUrl; // Assuming backend provides the URL
          console.log('Image URL:', imageUrl);
          console.log('ok');
      
          // Set the image source using the URL
          setImageUrl(imageUrl); // Update state with the image URL
        } catch (error) {
          console.error('Error uploading file:', error);
        }
    };

    return (
        <div>
            <h1>Tạo Team</h1>
            <input type="file" onChange={handleFileChange} />
            {imageUrl && <img src={imageUrl} alt="Team logo" />} {/* Hiển thị ảnh chỉ khi imageUrl có giá trị */}
            <button onClick={handleSaveTeam}>Lưu</button>
        </div>
    );
}
