import React, { useState } from 'react';
import axios from 'axios';

export default function Register() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [phone, setPhone] = useState('');
    const [namePlayer, setNamePlayer] = useState('');
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleRegister = async () => {
        try {
            const response = await axios.post('http://localhost:3001/auth/register', {
                email,
                password,
                phone,
                namePlayer
            });
            console.log(response.data); // Log kết quả từ backend
            setSuccessMessage('Đăng ký thành công!'); // Cập nhật thông báo thành công
            // Có thể thực hiện thêm các hành động khác sau khi đăng ký thành công
        } catch (error) {
            setError(error.response.data.message); // Hiển thị thông báo lỗi từ backend
        }
    };

    return (
        <div>
            <h1>Đăng ký</h1>
            <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
            /><br />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            /><br />
            <input
                type="text"
                placeholder="Phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
            /><br />
            <input
                type="text"
                placeholder="Name Player"
                value={namePlayer}
                onChange={(e) => setNamePlayer(e.target.value)}
            /><br />
            <button onClick={handleRegister}>Register</button>
            {error && <p>{error}</p>}
            {successMessage && <p>{successMessage}</p>}
        </div>
    );
}
