import React, { useState } from 'react';
import axios from 'axios';

export default function CreateTeam() {
    const [nameTeam, setNameTeam] = useState('');
    const [logo, setLogo] = useState('');
    const [colorShirt, setColorShirt] = useState('');
    const [player, setPlayer] = useState({}); // Thông tin của cầu thủ
    const [emailOrPhone, setEmailOrPhone] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            // Tạo đội bóng với thông tin của cầu thủ
            const teamData = { nameTeam, logo, colorShirt };
            const response = await axios.post('http://localhost:3001/teams/createTeam', teamData);
            console.log(response.data);

            // Thêm cầu thủ vào đội bóng
            if (player._id) {
                await axios.post('http://localhost:3001/add-player-to-team/add-to-team', {
                    playerId: player._id,
                    teamId: response.data._id // Lấy ID của đội bóng vừa tạo
                });
            }

            // Reset form after successful submission
            setNameTeam('');
            setLogo('');
            setColorShirt('');
            setPlayer({});
            setEmailOrPhone('');
            alert('Team created successfully!');
        } catch (error) {
            console.error('Error creating team:', error);
            alert('Failed to create team. Please try again.');
        }
    };

    const handleAddPlayer = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/players?search=${emailOrPhone}`);
            if (response.data) {
                setPlayer(response.data); // Cài đặt thông tin của cầu thủ tìm thấy
            }
        } catch (error) {
            console.error('Error add player:', error);
            alert('Failed to add player. Please try again.');
        }
    };

    return (
        <div>
            <h1>Create Team</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="nameTeam">Team Name:</label>
                <input type="text" id="nameTeam" name="nameTeam" value={nameTeam} onChange={(e) => setNameTeam(e.target.value)} />
                <br />
                <label htmlFor="logo">Logo:</label>
                <input type="text" id="logo" name="logo" value={logo} onChange={(e) => setLogo(e.target.value)} />
                <br />
                <label htmlFor="colorShirt">Color Shirt:</label>
                <input type="text" id="colorShirt" name="colorShirt" value={colorShirt} onChange={(e) => setColorShirt(e.target.value)} />
                <br />
                <label htmlFor="emailOrPhone">Email or Phone of Player:</label>
                <input type="text" id="emailOrPhone" name="emailOrPhone" value={emailOrPhone} onChange={(e) => setEmailOrPhone(e.target.value)} />
                <button type="button" onClick={handleAddPlayer}>Add Player</button>
                <br />
                {player.name && (
                    <div>
                        <h3>Player Info:</h3>
                        <p>Name: {player.name}</p>
                        <p>Email: {player.email}</p>
                        <p>Phone: {player.phone}</p>
                        <p>Age: {player.age}</p>
                        <p>Shirt Number: {player.shirtNumber}</p>
                    </div>
                )}
                <button type="submit">Create Team</button>
            </form>
        </div>
    );
}
