import React, { useState } from 'react';
import axios from 'axios';

export default function CreateTeam() {
    const [nameTeam, setNameTeam] = useState('');
    const [logo, setLogo] = useState('');
    const [colorShirt, setColorShirt] = useState('');
    const [players, setPlayers] = useState([]); // Thông tin của các cầu thủ
    const [emailOrPhone, setEmailOrPhone] = useState('');

    const handleAddPlayer = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/find/players?phone=${emailOrPhone}`);
            console.log(response.data);
            if (response.data) {
                setPlayers([...players, response.data]); // Thêm thông tin của cầu thủ vào danh sách cầu thủ
                setEmailOrPhone(''); // Xóa giá trị từ emailOrPhone để người dùng có thể nhập thông tin cầu thủ tiếp theo
            }
        } catch (error) {
            console.error('Error add player:', error);
            alert('Failed to add player. Please try again.');
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            // Tạo đội bóng với thông tin của cầu thủ
            const teamData = { nameTeam, logo, colorShirt, players };
            const response = await axios.post('http://localhost:3001/teams/createTeam', teamData);
            console.log(response.data);

            setNameTeam('');
            setLogo('');
            setColorShirt('');
            setPlayers([]);
            alert('Team created successfully!');
        } catch (error) {
            console.error('Error creating team:', error);
            alert('Failed to create team. Please try again.');
        }
    };

    const handleShirtNumberChange = (event, index) => {
        const updatedPlayers = [...players];
        updatedPlayers[index].shirtNumber = event.target.value;
        setPlayers(updatedPlayers);
    };

    return (
        <div class="create-team-container">
        <div class="create-team-form">
            <h1>Create Team</h1>
            <form onSubmit={handleSubmit} class="team-form">
                <label htmlFor="nameTeam" class="team-label">Team Name:</label>
                <input type="text" id="nameTeam" name="nameTeam"
                    value={nameTeam}
                    onChange={(e) => setNameTeam(e.target.value)}
                    class="team-input" />
                <br />
                <label htmlFor="logo" class="team-label">Logo:</label>
                <input type="text" id="logo" name="logo"
                    value={logo}
                    onChange={(e) => setLogo(e.target.value)}
                    class="team-input" />
                <br />
                <label htmlFor="colorShirt" class="team-label">Color Shirt:</label>
                <input type="text" id="colorShirt" name="colorShirt"
                    value={colorShirt}
                    onChange={(e) => setColorShirt(e.target.value)}
                    class="team-input" />
                <br />
                <label htmlFor="emailOrPhone" class="team-label">Email or Phone of Player:</label>
                <input type="text" id="emailOrPhone" name="emailOrPhone"
                    value={emailOrPhone}
                    onChange={(e) => setEmailOrPhone(e.target.value)}
                    class="team-input" />
                <button type="button" onClick={handleAddPlayer} class="add-player-button">Add Player</button>
                <br />
                {players.length > 0 && (
                    <div class="players-container">
                        <h3 class="players-heading">Players:</h3>
                        {players.map((player, index) => (
                            <div key={index} class="player-details">
                                <h4 class="player-heading">Player {index + 1}:</h4>
                                <p class="player-info">Name: {player.namePlayer}</p>
                                <p class="player-info">Email: {player.email}</p>
                                <p class="player-info">Phone: {player.phone}</p>
                                <p class="player-info">Age: {player.age}</p>
                                <p class="player-info">Shirt Number: <input type="text"
                                    value={player.shirtNumber}
                                    onChange={(e) => handleShirtNumberChange(e, index)}
                                    class="shirt-number-input" /></p>
                            </div>
                        ))}
                    </div>
                )}
                <button type="submit" class="create-team-button">Create Team</button>
            </form>
        </div>
    </div>
    
    );
}
