import React, { useState } from "react";
import axios from "axios";

export default function Login() {
    const [emailOrPhone, setEmailOrPhone] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const loginData = {
                emailOrPhone: emailOrPhone,
                password: password
            };

            const response = await axios.post('http://localhost:3001/auth/login', loginData);
            console.log(response.data); // Log kết quả từ be
            if (response.data.accessToken) {
                // Lưu trữ token vào localStorage
                localStorage.setItem('accessToken', response.data.accessToken);
                window.alert('Đăng nhập thành công!');
                // Xử lý sau khi đăng nhập thành công, ví dụ: chuyển hướng hoặc làm gì đó
            }
        } catch (error) {
            window.alert('Đăng nhập không thành công! Vui lòng kiểm tra lại thông tin.');
        }
    };

    return (
        <div class="login-container">
        <h1 class="login-title">Login</h1>
        <form class="login-form" onSubmit={handleSubmit}>
            <label class="login-label" htmlFor="emailOrPhone">Email or Phone:</label><br/>
            <input 
                type="text" 
                id="emailOrPhone" 
                name="emailOrPhone"
                value={emailOrPhone}
                onChange={(e) => setEmailOrPhone(e.target.value)}
                class="login-input"
            /><br/><br/>
            <label class="login-label" htmlFor="password">Password:</label><br/>
            <input 
                type="password" 
                id="password" 
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                class="login-input"
            /><br/><br/>
            <input type="submit" value="Log in" class="login-submit" />
        </form>
    </div>
    
    );
}
