import React, { useState } from "react";
import axios from "axios";

export default function Login() {
    const [emailOrPhone, setEmailOrPhone] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const loginData = {
                emailOrPhone: emailOrPhone,
                password: password
            };
    
            const response = await axios.post('http://localhost:3000/auth/login', loginData);
            console.log(response.data); // Log kết quả từ backend
    
            if (response.status === 200) {
                // Xử lý sau khi đăng nhập thành công
                console.log('Đăng nhập thành công!');
            } else {
                // Xử lý khi đăng nhập không thành công (ví dụ: hiển thị thông báo lỗi)
                setError('Đăng nhập không thành công');
            }
        } catch (error) {
            setError(error.response.data.message); // Hiển thị thông báo lỗi từ backend
        }
    };
        

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="emailOrPhone">Email or Phone:</label><br/>
                <input 
                    type="text" 
                    id="emailOrPhone" 
                    name="emailOrPhone"
                    value={emailOrPhone}
                    onChange={(e) => setEmailOrPhone(e.target.value)}
                /><br/><br/>
                <label htmlFor="password">Password:</label><br/>
                <input 
                    type="password" 
                    id="password" 
                    name="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                /><br/><br/>
                <input type="submit" value="Log in" />
            </form>
            {error && <p>{error}</p>}
        </div>
    );
}
