import React, { useState } from "react";

export default function CreateTeam() {
    const [teamAvatar, setTeamAvatar] = useState('');
    const [teamName, setTeamName] = useState('');
    const [contactName, setContactName] = useState('');
    const [contactPhone, setContactPhone] = useState('');

    const handleTeamAvatarChange = (event) => {
        // Xử lý khi người dùng chọn ảnh đại diện đội bóng
        // Ví dụ: setTeamAvatar(event.target.files[0]);
    };

    const handleSaveTeam = () => {
        // Xử lý khi người dùng nhấn nút lưu
        // Ví dụ: gửi dữ liệu tạo team lên server
    };

    return (
        <div>
            <h1>Tạo Team</h1>
            <label htmlFor="teamAvatar">Ảnh đại diện đội bóng:</label><br/>
            <input 
                type="file" 
                id="teamAvatar" 
                name="teamAvatar"
                onChange={handleTeamAvatarChange}
            /><br/><br/>
            <label htmlFor="teamName">Tên đội:</label><br/>
            <input 
                type="text" 
                id="teamName" 
                name="teamName"
                value={teamName}
                onChange={(e) => setTeamName(e.target.value)}
            /><br/><br/>
            <label htmlFor="contactName">Tên người liên hệ:</label><br/>
            <input 
                type="text" 
                id="contactName" 
                name="contactName"
                value={contactName}
                onChange={(e) => setContactName(e.target.value)}
            /><br/><br/>
            <label htmlFor="contactPhone">Số điện thoại người liên hệ:</label><br/>
            <input 
                type="text" 
                id="contactPhone" 
                name="contactPhone"
                value={contactPhone}
                onChange={(e) => setContactPhone(e.target.value)}
            /><br/><br/>
            <button onClick={handleSaveTeam}>Lưu</button>
        </div>
    );
}
