import React, { useState, useEffect } from 'react';

export default function FindTeam(){

    const [teams, setTeams] = useState([]);

    useEffect(() => {
        // Gửi yêu cầu API để lấy thông tin của tất cả các đội
        fetchTeams();
    }, []);

    const fetchTeams = async () => {
        try {
            const response = await fetch('http://localhost:3001/find/teams'); // Thay đổi đường dẫn API theo cấu trúc của ứng dụng của bạn
            const data = await response.json();
            setTeams(data);
        } catch (error) {
            console.error('Error fetching teams:', error);
        }
    };

    return(
        <div>
            <h1>All Teams</h1>
            <ul>
                {teams.map(team => (
                    <li key={team._id}>
                        <h2>{team.name}</h2>
                        <p>Logo: <img src={team.logo} alt="Team Logo" /></p>
                        <p>Color Shirt: {team.colorShirt}</p>
                        <h3>Players:</h3>
                        <ul>
                            {team.players.map(player => (
                                <li key={player._id}>
                                    Name: {player.name}, Email: {player.email}, Phone: {player.phone}, Age: {player.age}, Shirt Number: {player.shirtNumber}
                                </li>
                            ))}
                        </ul>
                    </li>
                ))}
            </ul>
        </div>
    )
}
